//Define Groovy Utils and holder for validating the XML response content
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def holder = groovyUtils.getXmlHolder(messageExchange.responseContent)
import groovy.util.Eval
import static  Math.abs
import org.junit.Assert


holder.namespaces["soap"]='http://schemas.xmlsoap.org/soap/envelope/';
holder.namespaces["ns1"]='http://www.webserviceX.NET/';

//Get actual converted weight
def ResponseConvertWeightResult = holder.getNodeValue("//ns1:ConvertWeightResult")
log.info(ResponseConvertWeightResult)

//Get 1KG to Grams conversion
def kgToGrams = context.expand('${WeightUnitsProperties#KG-Grams}');
def weightToConvertInKG = context.expand('${#TestSuite#Unit100}');

//Get expected Converted Weight
def GetExpectedConvertedWeight = String.format("%.0f", new BigDecimal((new java.math.BigDecimal(kgToGrams) * new java.math.BigDecimal(weightToConvertInKG))))

//Check that actual and expected result are the same
//ResponseConvertWeightResult==GetExpectedConvertedWeight

Assert.assertEquals(ResponseConvertWeightResult,GetExpectedConvertedWeight);

